import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EquipmentManager.css';

const ITEquipment = () => {
  const [equipments, setEquipments] = useState([]);
  const [newEquipmentName, setNewEquipmentName] = useState('');

  useEffect(() => {
    const fetchEquipments = async () => {
      const response = await axios.get('/api/equipments');
      setEquipments(response.data);
    };

    fetchEquipments();
  }, []);

  const handleAddEquipment = async (position) => {
    const response = await axios.post('/api/equipments', { name: newEquipmentName });
    const newEquipment = response.data;
    
    if (position === 'above') {
      setEquipments([newEquipment, ...equipments]);
    } else if (position === 'below') {
      setEquipments([...equipments, newEquipment]);
    }
    
    setNewEquipmentName('');
  };

  const handleDeleteEquipment = async (id) => {
    await axios.delete(`/api/equipments/${id}`);
    setEquipments(equipments.filter(equipment => equipment.id !== id));
  };

  return (
    <div className="equipment-manager">
      <h1>Equipment Manager</h1>
      <div className="add-equipment">
        <input
          type="text"
          value={newEquipmentName}
          onChange={(e) => setNewEquipmentName(e.target.value)}
          placeholder="New equipment name"
        />
        <button onClick={() => handleAddEquipment('below')}>Add Equipment Below</button>
      </div>
      <div className="table-container">
        <div className="table-actions">
          <button onClick={() => handleAddEquipment('above')} className="action-button">Add Row Above</button>
          <button onClick={() => handleAddEquipment('below')} className="action-button">Add Row Below</button>
          <button className="action-button delete">Delete Selected</button>
        </div>
        <table className="data-table">
          <thead>
            <tr>
              <th>#</th>
              <th>Equipment Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {equipments.map((equipment, index) => (
              <tr key={equipment.id}>
                <td>{index + 1}</td>
                <td>{equipment.name}</td>
                <td>
                  <button onClick={() => handleDeleteEquipment(equipment.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EquipmentManager;
