import React, { useState, useEffect } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';
import Select from 'react-select';
import './ExcelEditor.css';

const ExcelEditor = () => {
  const [selectedTable, setSelectedTable] = useState('');
  const [schema, setSchema] = useState([]);
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [tables, setTables] = useState([
    { value: 'telecom_pack', label: 'Parc Telecom' },
    { value: 'telephone_lines', label: 'Ligne Téléphonique' },
    { value: 'it_equipments', label: 'Ligne Téléphonique' },
    // Add more tables as needed
  ]);

  useEffect(() => {
    const fetchSchema = async (tableName) => {
      if (tableName) {
        const response = await axios.get(`http://localhost:5000/api/upload/schema/${tableName}`);
        setSchema(response.data);
      }
    };

    fetchSchema(selectedTable);
  }, [selectedTable]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedTable || !file) {
      setMessage('Please select a table and upload a file.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(`http://localhost:5000/api/upload/${selectedTable}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage(response.data.message);
    } catch (error) {
      setMessage('Upload failed: ' + error.response.data.error);
    }
  };

  return (
    <div className="excel-editor">
      <h2>Excel Data Uploader</h2>
      <div className="form-group">
        <label>Select Table</label>
        <Select
          value={tables.find(table => table.value === selectedTable)}
          onChange={(option) => setSelectedTable(option.value)}
          options={tables}
        />
      </div>
      {schema.length > 0 && (
        <div className="schema-info">
          <h3>Table Schema</h3>
          <table>
            <thead>
              <tr>
                <th>Column Name</th>
                <th>Data Type</th>
              </tr>
            </thead>
            <tbody>
              {schema.map((col, index) => (
                <tr key={index}>
                  <td>{col.column_name}</td>
                  <td>{col.data_type}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      <div className="form-group">
        <label>Upload Excel File</label>
        <input type="file" onChange={handleFileChange} />
      </div>
      <button onClick={handleUpload} className="upload-button">Upload</button>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default ExcelEditor;

