import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import { useTable, useSortBy, useFilters } from 'react-table';
import Select from 'react-select';
import './TelecomPack.css';

const setDefaultValues = (data, defaultValue = '') => {
  return Object.fromEntries(
    Object.entries(data).map(([key, value]) => {
      if (value === '' || value === null) {
        return [key, defaultValue];
      }
      return [key, value];
    })
  );
};

const getCustomHeaderName = (header) => {
  const customNames = {
    entite: 'Entite',
    operateur: 'Operateur',
    produit: 'Produit',
    produit2: 'Sous-Produit',
    numero: 'Numero',
    etatAbonnement: 'Etat d\'Abonnement',
    dateAbonnement: 'Date d\'Abonnement',
    dateReengagement: 'Date de Reengagement',
    dateEtat: 'Date d\'Etat',
    observation: 'Observation',
    typePoste: 'Type de Poste',
    numeroDeSerie: 'Numero de Serie',
    dateAffectation: 'Date d\'Affectation'
  };
  return customNames[header] || header.replace(/_/g, ' ');
};

const formatDate = (dateString) => {
  if (!dateString || dateString === '------') return '';
  const date = new Date(dateString);
  return date.toISOString().split('T')[0];
};

const TelecomPack = () => {
  const [telecomPacks, setTelecomPacks] = useState([]);
  const [originalData, setOriginalData] = useState([]);
  const [options, setOptions] = useState({
    entite: [],
    operateur: [],
    produit: ['DATA', 'VOIX', 'MOBILE', 'INTERNET'],
    etatAbonnement: [],
  });
  const [filters, setFilters] = useState({});
  const [newPack, setNewPack] = useState({
    entite: '',
    operateur: '',
    produit: '',
    produit2: '',
    numero: '',
    etatAbonnement: '',
    dateAbonnement: '',
    dateReengagement: '',
    dateEtat: '',
    observation: '',
    typePoste: '',        // Added
    numeroDeSerie: '',    // Added
    dateAffectation: ''   // Added
  });
  const [isEditing, setIsEditing] = useState(false);
  const [currentPack, setCurrentPack] = useState(null);
  const history = useHistory();
  const [currentPage, setCurrentPage] = useState(1);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  const fetchDropdownOptions = async () => {
    try {
      const fields = ['entite', 'operateur', 'etatAbonnement'];
      const fetchedOptions = {};
      for (const field of fields) {
        const response = await axios.get(`http://localhost:5000/api/telecom-packs/dropdown/${field}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const uniqueValues = Array.from(new Set(response.data.filter(value => value !== '')));
        fetchedOptions[field] = uniqueValues;
      }

      fetchedOptions.produit = ['DATA', 'VOIX', 'MOBILE', 'INTERNET'];

      setOptions(fetchedOptions);
    } catch (error) {
      console.error('Error fetching dropdown options:', error.message);
      alert('Failed to fetch dropdown options: ' + error.message);
    }
  };

  useEffect(() => {
    const fetchTelecomPacks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/telecom-packs', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = response.data.map(pack => {
          const formattedPack = setDefaultValues(pack);
          return {
            ...formattedPack,
            dateAbonnement: formatDate(formattedPack.dateAbonnement),
            dateReengagement: formatDate(formattedPack.dateReengagement),
            dateEtat: formatDate(formattedPack.dateEtat),
          };
        });

        setTelecomPacks(data);
        setOriginalData(data);
      } catch (error) {
        console.error('Error fetching Telecom Packs:', error.message);
        alert('Failed to fetch telecom packs: ' + error.message);
      }
    };

    fetchTelecomPacks();
    fetchDropdownOptions();
  }, [filters]);

  const handleAddPack = async () => {
    if (!newPack.entite) {
      alert('The "entite" field must be filled.');
      return;
    }

    try {
      const formattedPack = setDefaultValues(newPack);
      const response = await axios.post('http://localhost:5000/api/telecom-packs', formattedPack, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      const addedPack = response.data;
      setTelecomPacks([...telecomPacks, addedPack]);
      setNewPack({
        entite: '',
        operateur: '',
        produit: '',
        produit2: '',
        numero: '',
        etatAbonnement: '',
        dateAbonnement: '',
        dateReengagement: '',
        dateEtat: '',
        observation: '',
        typePoste: '',        // Added
        numeroDeSerie: '',    // Added
        dateAffectation: ''   // Added
      });
    } catch (error) {
      console.error('Error adding Telecom Pack:', error.message);
      alert('Failed to add telecom pack: ' + error.message);
    }
  };

  const handleModifyPack = (pack) => {
    setIsEditing(true);
    setCurrentPack(pack);
  };

  const handleUpdatePack = async () => {
    try {
      const formattedPack = setDefaultValues(currentPack);
      const response = await axios.put(`http://localhost:5000/api/telecom-packs/${currentPack.id}`, formattedPack, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      const updatedPack = response.data;
      setTelecomPacks(telecomPacks.map(pack => pack.id === updatedPack.id ? updatedPack : pack));
      setIsEditing(false);
      setCurrentPack(null);
    } catch (error) {
      console.error('Error updating Telecom Pack:', error.message);
      alert('Failed to update telecom pack: ' + error.message);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (isEditing) {
      setCurrentPack(prevState => ({
        ...prevState,
        [name]: value,
      }));
    } else {
      setNewPack(prevState => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  const handleDeletePack = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/telecom-packs/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setTelecomPacks(telecomPacks.filter(pack => pack.id !== id));
    } catch (error) {
      console.error('Error deleting Telecom Pack:', error.message);
      alert('Failed to delete telecom pack: ' + error.message);
    }
  };

  const handleFilterChange = (field, selectedOptions) => {
    const values = selectedOptions ? selectedOptions.map(option => option.value) : [];
    setFilters(prevFilters => ({ ...prevFilters, [field]: values }));
  };

  const columns = useMemo(() => [
    {
      Header: '#',
      accessor: (row, i) => i + 1,
      disableFilters: true,
      disableSortBy: true,
    },
    {
      Header: 'Actions',
      accessor: 'actions',
      disableFilters: true,
      disableSortBy: true,
      Cell: ({ row }) => (
        <div>
          <button className="modify-button" onClick={() => handleModifyPack(row.original)}>Modify</button>
          <button className="delete-button" onClick={() => handleDeletePack(row.original.id)}>Delete</button>
        </div>
      ),
    },
    ...Object.keys(newPack).map((key) => ({
      Header: getCustomHeaderName(key),
      accessor: key,
      Filter: ({ column: { filterValue, setFilter, preFilteredRows, id } }) => {
        const options = useMemo(() => {
          const optionsSet = new Set();
          preFilteredRows.forEach(row => {
            optionsSet.add(row.values[id]);
          });
          return [...optionsSet].map(option => ({ value: option, label: option }));
        }, [id, preFilteredRows]);

        const handleChange = (selectedOptions) => {
          setFilter(selectedOptions ? selectedOptions.map(option => option.value) : undefined);
        };

        return (
          <Select
            value={options.filter(option => filterValue && filterValue.includes(option.value))}
            onChange={handleChange}
            options={options}
            isMulti
            placeholder={`Filtrer par ${getCustomHeaderName(id)}`}
            className="filter-select"
          />
        );
      }
    })),
  ], [newPack]);

  const filteredData = useMemo(() => {
    return telecomPacks.filter(pack => {
      return columns.every(column => {
        if (!column.filterValue || column.filterValue.length === 0) return true;
        const cellValue = pack[column.accessor];
        return column.filterValue.includes(cellValue);
      });
    });
  }, [telecomPacks, columns]);

  const paginatedData = useMemo(() => {
    const startIndex = (currentPage - 1) * rowsPerPage;
    const endIndex = startIndex + rowsPerPage;
    return filteredData.slice(startIndex, endIndex);
  }, [filteredData, currentPage, rowsPerPage]);

  return (
    <div className="telecom-pack-manager">
      <button className="telecompack-modify-back-button" onClick={() => history.goBack()}>
        &#x21a9;
      </button>
      <h1>Telecom Pack Manager</h1>
      <div className="add-pack">
        <table className="form-table telecom-pack-form-table">
          <tbody>
            {Object.keys(newPack).map((key, index) => (
              index % 3 === 0 && (
                <tr key={index}>
                  {Object.keys(newPack).slice(index, index + 3).map(innerKey => (
                    <td key={innerKey}>
                      <label className="telecom-pack-form-label">{getCustomHeaderName(innerKey)}</label>
                      <input
                        type={innerKey.startsWith('date') ? 'date' : 'text'}
                        name={innerKey}
                        value={isEditing ? currentPack[innerKey] : newPack[innerKey]}
                        onChange={handleChange}
                        className="input-field"
                        placeholder={`Entrer ${innerKey.replace(/_/g, ' ')}`}
                      />
                    </td>
                  ))}
                </tr>
              )
            ))}
          </tbody>
        </table>
        <button className="add-pack" onClick={isEditing ? handleUpdatePack : handleAddPack}>
          {isEditing ? 'Update Pack' : 'Add Pack'}
        </button>
      </div>
      <div className="table-container">
        <Table columns={columns} data={paginatedData} />
      </div>
      <div className="pagination-controls">
        <button onClick={() => setCurrentPage(1)} disabled={currentPage === 1}>{'<<'}</button>
        <button onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage === 1}>{'PrÃ©cÃ©dent'}</button>
        <span>Page {currentPage} of {Math.ceil(filteredData.length / rowsPerPage)}</span>
        <button onClick={() => setCurrentPage(currentPage + 1)} disabled={currentPage === Math.ceil(filteredData.length / rowsPerPage)}>{'Suivant'}</button>
        <button onClick={() => setCurrentPage(Math.ceil(filteredData.length / rowsPerPage))} disabled={currentPage === Math.ceil(filteredData.length / rowsPerPage)}>{'>>'}</button>
        <select value={rowsPerPage} onChange={(e) => setRowsPerPage(Number(e.target.value))}>
          <option value={10}>Show 10</option>
          <option value={25}>Show 25</option>
          <option value={50}>Show 50</option>
          <option value={100}>Show 100</option>
        </select>
      </div>
    </div>
  );
};

const Table = ({ columns, data }) => {
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable(
    {
      columns,
      data,
    },
    useFilters,
    useSortBy
  );

  return (
    <table {...getTableProps()} className="data-table telecompack-view-data-table">
      <thead>
        {headerGroups.map(headerGroup => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map(column => (
              <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                <div>
                  {column.render('Header')}
                  <span>
                    {column.isSorted
                      ? column.isSortedDesc
                        ? ' ðŸ”½'
                        : ' ðŸ”¼'
                      : ''}
                  </span>
                  <div>{column.canFilter ? column.render('Filter') : null}</div>
                </div>
              </th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row, index) => {
          prepareRow(row);
          return (
            <tr {...row.getRowProps()} className={index % 2 === 0 ? "telecompack-view-row-even" : "telecompack-view-row-odd"}>
              {row.cells.map(cell => (
                <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
              ))}
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

export default TelecomPack;
