// src/components/Dashboard/ExcelEditor.js
import React, { useState } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';

const ExcelEditor = () => {
  const [sheets, setSheets] = useState([]);
  const [fileName, setFileName] = useState('');

  const handleFileUpload = async (e) => {
    try {
      const file = e.target.files[0];
      setFileName(file.name);

      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('/api/excel/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (response.data.sheets && response.data.sheets.length > 0) {
        setSheets(response.data.sheets);
      } else {
        console.error('No sheets data found in the uploaded file.');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const handleCellChange = (sheetIndex, rowIndex, colKey, value) => {
    const updatedSheets = [...sheets];
    updatedSheets[sheetIndex].data[rowIndex][colKey] = value;
    setSheets(updatedSheets);
  };

  const downloadExcel = async () => {
    try {
      const response = await axios.post('/api/excel/generate', { sheets }, { responseType: 'arraybuffer' });
      const blob = new Blob([response.data], { type: response.headers['content-type'] });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'modified_' + fileName;
      document.body.appendChild(a);
      a.click();
      a.remove();
    } catch (error) {
      console.error('Error downloading file:', error);
    }
  };

  return (
    <div>
      <input type="file" onChange={handleFileUpload} />
      {sheets.map((sheet, sheetIndex) => (
        <div key={sheet.sheetName}>
          <h3>{sheet.sheetName}</h3>
          <table>
            <thead>
              <tr>
                {sheet.data.length > 0 && Object.keys(sheet.data[0]).map(colKey => (
                  <th key={colKey}>{colKey}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {sheet.data.map((row, rowIndex) => (
                <tr key={rowIndex}>
                  {Object.keys(row).map(colKey => (
                    <td key={colKey}>
                      <input
                        type="text"
                        value={row[colKey]}
                        onChange={(e) => handleCellChange(sheetIndex, rowIndex, colKey, e.target.value)}
                      />
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
      <button onClick={downloadExcel}>Download Modified Excel</button>
    </div>
  );
};

export default ExcelEditor;
