import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import './Dashboard.css';

const AdminDashboard = () => {
  const { user } = useAuth();
  const history = useHistory();
  const [userHistory, setUserHistory] = useState([]);

  const handleNavigation = (path) => {
    history.push(path);
  };

  useEffect(() => {
    // Fetch the history of users' last login
    const fetchUserHistory = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/admin/user-history', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setUserHistory(data);
        } else {
          console.error('Failed to fetch user history');
        }
      } catch (error) {
        console.error('Error fetching user history:', error);
      }
    };

    fetchUserHistory();
  }, []);

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <div className="sidebar-item" onClick={() => handleNavigation('/dashboard')}>
          Dashboard
        </div>
        <div className="sidebar-item" onClick={() => handleNavigation('/telecom-packs')}>
          Telecom Packs
        </div>
        <div className="sidebar-item" onClick={() => handleNavigation('/telephone-lines')}>
          Telephone Lines
        </div>
        <div className="sidebar-item" onClick={() => handleNavigation('/it-equipments')}>
          IT Equipments
        </div>
      </aside>
      <div className="dashboard-content">
        <h1>Admin Dashboard</h1>
        <p>Welcome, {user.fullName}!</p>
        <div className="profile-section">
          <h2>Your Profile</h2>
          <p><strong>Email:</strong> {user.email}</p>
        </div>
        <div className="user-history-section">
          <h2>User History</h2>
          {userHistory.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Full Name</th>
                  <th>Email</th>
                  <th>Last Login</th>
                </tr>
              </thead>
              <tbody>
                {userHistory.map((user, index) => (
                  <tr key={index}>
                    <td>{user.fullName}</td>
                    <td>{user.email}</td>
                    <td>{new Date(user.lastLogin).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No user login history available.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
