import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ITEquipment.css';

const ITEquipment = () => {
  const [itEquipments, setITEquipments] = useState([]);
  const [options, setOptions] = useState({});
  const [newEquipment, setNewEquipment] = useState({
    categorie: '',
    marque: '',
    model: '',
    code_materiel: '',
    serie: '',
    code_localisation: '',
    code_entite: '',
    date_installation: '',
    fin_garantie: '',
    statut: '',
    type_acquisition: '',
    date_achat: '',
    date_livraison: '',
    fournisseur: '',
    numero_facture: '',
    prix_achat: '',
    numero_appel_offre: '',
    numero_livraison: '',
    cout_maintenance: '',
    emploi_principal: '',
    niveau_criticite: '',
    sla: '',
    date_sortie: '',
  });
  const [isEditing, setIsEditing] = useState(false);
  const [currentEquipment, setCurrentEquipment] = useState(null);

  useEffect(() => {
    const fetchITEquipments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/it-equipments', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setITEquipments(response.data);
        console.log('Fetched IT Equipments:', response.data);

        // Extract options from the fetched data
        const extractedOptions = response.data.reduce((acc, equipment) => {
          for (const key in equipment) {
            if (!acc[key]) {
              acc[key] = [];
            }
            if (equipment[key] && !acc[key].includes(equipment[key])) {
              acc[key].push(equipment[key]);
            }
          }
          return acc;
        }, {});
        setOptions(extractedOptions);

      } catch (error) {
        console.error('Error fetching IT Equipments:', error);
      }
    };

    fetchITEquipments();
  }, []);

  const handleAddEquipment = async () => {
    try {
      // Check if at least one field is non-null
      const hasNonNullField = Object.values(newEquipment).some(value => value.trim() !== '');
      if (!hasNonNullField) {
        alert('Please fill in at least one field.');
        return;
      }

      const formattedEquipment = {
        ...newEquipment,
        date_installation: newEquipment.date_installation || null,
        fin_garantie: newEquipment.fin_garantie || null,
        date_achat: newEquipment.date_achat || null,
        date_livraison: newEquipment.date_livraison || null,
        date_sortie: newEquipment.date_sortie || null,
      };

      console.log('Adding equipment:', formattedEquipment);
      const response = await axios.post('http://localhost:5000/api/it-equipments', formattedEquipment, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      console.log('Response from server:', response.data);

      const addedEquipment = response.data;

      setITEquipments([...itEquipments, addedEquipment]);
      setNewEquipment({
        categorie: '',
        marque: '',
        model: '',
        code_materiel: '',
        serie: '',
        code_localisation: '',
        code_entite: '',
        date_installation: '',
        fin_garantie: '',
        statut: '',
        type_acquisition: '',
        date_achat: '',
        date_livraison: '',
        fournisseur: '',
        numero_facture: '',
        prix_achat: '',
        numero_appel_offre: '',
        numero_livraison: '',
        cout_maintenance: '',
        emploi_principal: '',
        niveau_criticite: '',
        sla: '',
        date_sortie: '',
      });
      console.log('Equipment added successfully');
    } catch (error) {
      console.error('Error adding IT Equipment:', error);
    }
  };

  const handleDeleteEquipment = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/it-equipments/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setITEquipments(itEquipments.filter(equipment => equipment.id !== id));
      console.log('Equipment deleted successfully');
    } catch (error) {
      console.error('Error deleting IT Equipment:', error);
    }
  };

  const handleModifyEquipment = (equipment) => {
    const filteredEquipment = Object.fromEntries(
      Object.entries(equipment).filter(([key, value]) => value !== '' && value !== null)
    );
    setIsEditing(true);
    setCurrentEquipment(filteredEquipment);
  };

  const handleUpdateEquipment = async () => {
    try {
      const filteredEquipment = Object.fromEntries(
        Object.entries(currentEquipment).filter(([key, value]) => value !== '' && value !== null)
      );

      console.log('Updating equipment:', filteredEquipment);
      const response = await axios.put(`http://localhost:5000/api/it-equipments/${currentEquipment.id}`, filteredEquipment, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      console.log('Response from server:', response.data);
      const updatedEquipment = response.data;
      setITEquipments(itEquipments.map(equipment => equipment.id === updatedEquipment.id ? updatedEquipment : equipment));
      setIsEditing(false);
      setCurrentEquipment(null);
      console.log('Equipment updated successfully');
    } catch (error) {
      console.error('Error updating IT Equipment:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (isEditing) {
      setCurrentEquipment(prevState => ({
        ...prevState,
        [name]: value,
      }));
    } else {
      setNewEquipment(prevState => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toISOString().split('T')[0];
  };

  const CustomDropdown = ({ name, value, options, onChange }) => {
    const [isOpen, setIsOpen] = useState(false);

    const handleSelect = (option) => {
      onChange({ target: { name, value: option } });
      setIsOpen(false);
    };

    return (
      <div className="dropdown-container">
        <input
          type="text"
          name={name}
          value={value}
          onChange={onChange}
        />
        <div className="dropdown-arrow" onClick={() => setIsOpen(!isOpen)}>
          ▼
        </div>
        {isOpen && (
          <div className="dropdown-menu">
            {options.map((option, index) => (
              <div key={index} className="dropdown-option" onClick={() => handleSelect(option)}>
                {option}
              </div>
            ))}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="it-equipment-manager">
      <h1>IT Equipment Manager</h1>
      <div className="add-equipment">
        <table className="form-table">
          <tbody>
            {Object.keys(newEquipment).map((key, index) => (
              <React.Fragment key={key}>
                {index % 4 === 0 && index !== 0 && <tr />}
                <td>
                  <label>{key.replace(/_/g, ' ')}</label>
                  {[
                    'categorie', 'marque', 'model', 'code_materiel', 'serie', 'code_localisation', 
                    'code_entite', 'statut', 'type_acquisition', 'fournisseur', 'emploi_principal', 
                    'niveau_criticite', 'sla',
                  ].includes(key) ? (
                    <CustomDropdown
                      name={key}
                      value={isEditing ? currentEquipment[key] : newEquipment[key]}
                      options={options[key] || []}
                      onChange={handleChange}
                    />
                  ) : (
                    <input
                      type="text"
                      name={key}
                      value={isEditing ? currentEquipment[key] : newEquipment[key]}
                      onChange={handleChange}
                      className="input-field"
                    />
                  )}
                </td>
              </React.Fragment>
            ))}
          </tbody>
        </table>
        {isEditing ? (
          <button onClick={handleUpdateEquipment}>Update Equipment</button>
        ) : (
          <button onClick={handleAddEquipment}>Add Equipment</button>
        )}
      </div>
      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>Field</th>
              {itEquipments.map((equipment, index) => (
                <th key={index}>Equipment {index + 1}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Object.keys(newEquipment).map((key) => (
              <tr key={key}>
                <td className="left-column">{key.replace(/_/g, ' ')}</td>
                {itEquipments.map((equipment, index) => (
                  <td key={index} className={index % 2 === 0 ? "right-column" : ""}>
                    {['date_installation', 'fin_garantie', 'date_achat', 'date_livraison', 'date_sortie'].includes(key)
                      ? formatDate(equipment[key])
                      : equipment[key]}
                  </td>
                ))}
              </tr>
            ))}
            <tr>
              <td className="left-column">Actions</td>
              {itEquipments.map((equipment, index) => (
                <td key={index} className="actions-column">
                  <button className="modify-button" onClick={() => handleModifyEquipment(equipment)}>Modify</button>
                  <button className="delete-button" onClick={() => handleDeleteEquipment(equipment.id)}>Delete</button>
                </td>
              ))}
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ITEquipment;
