import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ITEquipment.css';

// Helper function to set default values
const setDefaultValues = (data, defaultValue = '------') => {
  return Object.fromEntries(
    Object.entries(data).map(([key, value]) => {
      if (value === '' || value === null) {
        if (['date_installation', 'fin_garantie', 'date_achat', 'date_livraison', 'date_sortie'].includes(key)) {
          return [key, null];  // Set date fields to null if empty
        } else {
          return [key, defaultValue];  // Set other fields to default value
        }
      }
      return [key, value];
    })
  );
};

const ITEquipment = () => {
  const [itEquipments, setITEquipments] = useState([]);
  const [options, setOptions] = useState({});
  const [newEquipment, setNewEquipment] = useState({
    categorie: '',
    marque: '',
    model: '',
    code_materiel: '',
    serie: '',
    code_localisation: '',
    code_entite: '',
    date_installation: '',
    fin_garantie: '',
    statut: '',
    type_acquisition: '',
    date_achat: '',
    date_livraison: '',
    fournisseur: '',
    numero_facture: '',
    prix_achat: '',
    numero_appel_offre: '',
    numero_livraison: '',
    cout_maintenance: '',
    emploi_principal: '',
    niveau_criticite: '',
    sla: '',
    date_sortie: '',
  });
  const [isEditing, setIsEditing] = useState(false);
  const [currentEquipment, setCurrentEquipment] = useState(null);

  useEffect(() => {
    const fetchITEquipments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/it-equipments', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setITEquipments(response.data);
        console.log('Fetched IT Equipments:', response.data);

        // Extract options from the fetched data
        const extractedOptions = response.data.reduce((acc, equipment) => {
          for (const key in equipment) {
            if (!acc[key]) {
              acc[key] = [];
            }
            if (equipment[key] && !acc[key].includes(equipment[key])) {
              acc[key].push(equipment[key]);
            }
          }
          return acc;
        }, {});
        setOptions(extractedOptions);

      } catch (error) {
        console.error('Error fetching IT Equipments:', error);
      }
    };

    fetchITEquipments();
  }, []);

  const handleAddEquipment = async () => {
    try {
      const formattedEquipment = setDefaultValues(newEquipment);
      console.log('Adding equipment:', formattedEquipment);
      const response = await axios.post('http://localhost:5000/api/it-equipments', formattedEquipment, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      console.log('Response from server:', response.data);

      const addedEquipment = response.data;

      setITEquipments([...itEquipments, addedEquipment]);
      setNewEquipment({
        categorie: '',
        marque: '',
        model: '',
        code_materiel: '',
        serie: '',
        code_localisation: '',
        code_entite: '',
        date_installation: '',
        fin_garantie: '',
        statut: '',
        type_acquisition: '',
        date_achat: '',
        date_livraison: '',
        fournisseur: '',
        numero_facture: '',
        prix_achat: '',
        numero_appel_offre: '',
        numero_livraison: '',
        cout_maintenance: '',
        emploi_principal: '',
        niveau_criticite: '',
        sla: '',
        date_sortie: '',
      });
      console.log('Equipment added successfully');
    } catch (error) {
      console.error('Error adding IT Equipment:', error);
    }
  };

  const handleDeleteEquipment = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/it-equipments/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setITEquipments(itEquipments.filter(equipment => equipment.id !== id));
      console.log('Equipment deleted successfully');
    } catch (error) {
      console.error('Error deleting IT Equipment:', error);
    }
  };

  const handleModifyEquipment = (equipment) => {
    const filteredEquipment = Object.fromEntries(
      Object.entries(equipment).filter(([key, value]) => value !== '' && value !== null)
    );
    setIsEditing(true);
    setCurrentEquipment(filteredEquipment);
  };

  const handleUpdateEquipment = async () => {
    try {
      const filteredEquipment = setDefaultValues(currentEquipment);
      console.log('Updating equipment:', filteredEquipment);
      const response = await axios.put(`http://localhost:5000/api/it-equipments/${currentEquipment.id}`, filteredEquipment, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      console.log('Response from server:', response.data);
      const updatedEquipment = response.data;
      setITEquipments(itEquipments.map(equipment => equipment.id === updatedEquipment.id ? updatedEquipment : equipment));
      setIsEditing(false);
      setCurrentEquipment(null);
      console.log('Equipment updated successfully');
    } catch (error) {
      console.error('Error updating IT Equipment:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (isEditing) {
      setCurrentEquipment(prevState => ({
        ...prevState,
        [name]: value,
      }));
    } else {
      setNewEquipment(prevState => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  const formatDate = (dateString) => {
    if (!dateString || dateString === '------') return '';
    const date = new Date(dateString);
    return date.toISOString().split('T')[0];
  };

  const CustomDropdown = ({ name, value, options, onChange, placeholder }) => {
    const [isOpen, setIsOpen] = useState(false);

    const handleSelect = (option) => {
      onChange({ target: { name, value: option } });
      setIsOpen(false);
    };

    return (
      <div className="dropdown-container">
        <input
          type="text"
          name={name}
          value={value}
          onChange={onChange}
          placeholder={placeholder} // Add placeholder
        />
        <div className="dropdown-arrow" onClick={() => setIsOpen(!isOpen)}>
          ▼
        </div>
        {isOpen && (
          <div className="dropdown-menu">
            {options.map((option, index) => (
              <div key={index} className="dropdown-option" onClick={() => handleSelect(option)}>
                {option}
              </div>
            ))}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="it-equipment-manager">
      <h1>IT Equipment Manager</h1>
      <div className="add-equipment">
        <table className="form-table">
          <tbody>
            <tr>
              <td>
                <label>categorie</label>
                <CustomDropdown
                  name="categorie"
                  value={isEditing ? currentEquipment.categorie : newEquipment.categorie}
                  options={options.categorie || []}
                  onChange={handleChange}
                  placeholder="Select a category" // Custom placeholder
                />
              </td>
              <td>
                <label>marque</label>
                <CustomDropdown
                  name="marque"
                  value={isEditing ? currentEquipment.marque : newEquipment.marque}
                  options={options.marque || []}
                  onChange={handleChange}
                  placeholder="Enter the brand" // Custom placeholder
                />
              </td>
              <td>
                <label>model</label>
                <CustomDropdown
                  name="model"
                  value={isEditing ? currentEquipment.model : newEquipment.model}
                  options={options.model || []}
                  onChange={handleChange}
                  placeholder="Enter the model" // Custom placeholder
                />
              </td>
              <td>
                <label>code materiel</label>
                <CustomDropdown
                  name="code_materiel"
                  value={isEditing ? currentEquipment.code_materiel : newEquipment.code_materiel}
                  options={options.code_materiel || []}
                  onChange={handleChange}
                  placeholder="Enter the material code" // Custom placeholder
                />
              </td>
            </tr>
            <tr>
              <td>
                <label>serie</label>
                <CustomDropdown
                  name="serie"
                  value={isEditing ? currentEquipment.serie : newEquipment.serie}
                  options={options.serie || []}
                  onChange={handleChange}
                  placeholder="Enter the series" // Custom placeholder
                />
              </td>
              <td>
                <label>code localisation</label>
                <CustomDropdown
                  name="code_localisation"
                  value={isEditing ? currentEquipment.code_localisation : newEquipment.code_localisation}
                  options={options.code_localisation || []}
                  onChange={handleChange}
                  placeholder="Enter the location code" // Custom placeholder
                />
              </td>
              <td>
                <label>code entite</label>
                <CustomDropdown
                  name="code_entite"
                  value={isEditing ? currentEquipment.code_entite : newEquipment.code_entite}
                  options={options.code_entite || []}
                  onChange={handleChange}
                  placeholder="Enter the entity code" // Custom placeholder
                />
              </td>
              <td>
                <label>date installation</label>
                <input
                  type="text"
                  name="date_installation"
                  value={isEditing ? currentEquipment.date_installation : newEquipment.date_installation}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Enter installation date (YYYY-MM-DD)" // Custom placeholder
                />
              </td>
            </tr>
            <tr>
              <td>
                <label>fin garantie</label>
                <input
                  type="text"
                  name="fin_garantie"
                  value={isEditing ? currentEquipment.fin_garantie : newEquipment.fin_garantie}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Enter end of warranty date (YYYY-MM-DD)" // Custom placeholder
                />
              </td>
              <td>
                <label>statut</label>
                <CustomDropdown
                  name="statut"
                  value={isEditing ? currentEquipment.statut : newEquipment.statut}
                  options={options.statut || []}
                  onChange={handleChange}
                  placeholder="Enter the status" // Custom placeholder
                />
              </td>
              <td>
                <label>type acquisition</label>
                <CustomDropdown
                  name="type_acquisition"
                  value={isEditing ? currentEquipment.type_acquisition : newEquipment.type_acquisition}
                  options={options.type_acquisition || []}
                  onChange={handleChange}
                  placeholder="Enter acquisition type" // Custom placeholder
                />
              </td>
              <td>
                <label>date achat</label>
                <input
                  type="text"
                  name="date_achat"
                  value={isEditing ? currentEquipment.date_achat : newEquipment.date_achat}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Enter purchase date (YYYY-MM-DD)" // Custom placeholder
                />
              </td>
            </tr>
            <tr>
              <td>
                <label>date livraison</label>
                <input
                  type="text"
                  name="date_livraison"
                  value={isEditing ? currentEquipment.date_livraison : newEquipment.date_livraison}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Enter delivery date (YYYY-MM-DD)" // Custom placeholder
                />
              </td>
              <td>
                <label>fournisseur</label>
                <CustomDropdown
                  name="fournisseur"
                  value={isEditing ? currentEquipment.fournisseur : newEquipment.fournisseur}
                  options={options.fournisseur || []}
                  onChange={handleChange}
                  placeholder="Entrer fournisseur" // Custom placeholder
                />
              </td>
              <td>
                <label>numero facture</label>
                <input
                  type="text"
                  name="numero_facture"
                  value={isEditing ? currentEquipment.numero_facture : newEquipment.numero_facture}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Entrer numero facture" // Custom placeholder
                />
              </td>
              <td>
                <label>prix achat</label>
                <input
                  type="text"
                  name="prix_achat"
                  value={isEditing ? currentEquipment.prix_achat : newEquipment.prix_achat}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Entrer prix achat" // Custom placeholder
                />
              </td>
            </tr>
            <tr>
              <td>
                <label>numero appel offre</label>
                <input
                  type="text"
                  name="numero_appel_offre"
                  value={isEditing ? currentEquipment.numero_appel_offre : newEquipment.numero_appel_offre}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Entrer numero_appel_offre" // Custom placeholder
                />
              </td>
              <td>
                <label>numero livraison</label>
                <input
                  type="text"
                  name="numero_livraison"
                  value={isEditing ? currentEquipment.numero_livraison : newEquipment.numero_livraison}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Entrer numero de livraison" // Custom placeholder
                />
              </td>
              <td>
                <label>cout maintenance</label>
                <input
                  type="text"
                  name="cout_maintenance"
                  value={isEditing ? currentEquipment.cout_maintenance : newEquipment.cout_maintenance}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="Entrer cout de maintenance" // Custom placeholder
                />
              </td>
              <td>
                <label>emploi principal</label>
                <CustomDropdown
                  name="emploi_principal"
                  value={isEditing ? currentEquipment.emploi_principal : newEquipment.emploi_principal}
                  options={options.emploi_principal || []}
                  onChange={handleChange}
                  placeholder="Entrer emploi principale" // Custom placeholder
                />
              </td>
            </tr>
            <tr>
              <td>
                <label>niveau criticite</label>
                <CustomDropdown
                  name="niveau_criticite"
                  value={isEditing ? currentEquipment.niveau_criticite : newEquipment.niveau_criticite}
                  options={options.niveau_criticite || []}
                  onChange={handleChange}
                  placeholder="Entrer niveau de criticité" // Custom placeholder
                />
              </td>
              <td>
                <label>sla</label>
                <CustomDropdown
                  name="sla"
                  value={isEditing ? currentEquipment.sla : newEquipment.sla}
                  options={options.sla || []}
                  onChange={handleChange}
                  placeholder="Entrer SLA" // Custom placeholder
                />
              </td>
              <td>
                <label>date sortie</label>
                <input
                  type="text"
                  name="date_sortie"
                  value={isEditing ? currentEquipment.date_sortie : newEquipment.date_sortie}
                  onChange={handleChange}
                  className="input-field"
                  placeholder="YYYY-MM-DD" // Custom placeholder
                />
              </td>
            </tr>
          </tbody>
        </table>
        {isEditing ? (
          <button onClick={handleUpdateEquipment}>Update Equipment</button>
        ) : (
          <button onClick={handleAddEquipment}>Add Equipment</button>
        )}
      </div>
      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>Field</th>
              {itEquipments.map((equipment, index) => (
                <th key={index}>Equipment {index + 1}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Object.keys(newEquipment).map((key) => (
              <tr key={key}>
                <td className="left-column">{key.replace(/_/g, ' ')}</td>
                {itEquipments.map((equipment, index) => (
                  <td key={index} className={index % 2 === 0 ? "right-column" : ""}>
                    {['date_installation', 'fin_garantie', 'date_achat', 'date_livraison', 'date_sortie'].includes(key)
                      ? formatDate(equipment[key])
                      : equipment[key]}
                  </td>
                ))}
              </tr>
            ))}
            <tr>
              <td className="left-column">Actions</td>
              {itEquipments.map((equipment, index) => (
                <td key={index} className="actions-column">
                  <button className="modify-button" onClick={() => handleModifyEquipment(equipment)}>Modify</button>
                  <button className="delete-button" onClick={() => handleDeleteEquipment(equipment.id)}>Delete</button>
                </td>
              ))}
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ITEquipment;
