// ITEquipmentView.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ITEquipmentView.css'; // Create this CSS file for styles

const ITEquipmentView = () => {
  const [itEquipments, setITEquipments] = useState([]);

  useEffect(() => {
    const fetchITEquipments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/it-equipments', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setITEquipments(response.data);
      } catch (error) {
        console.error('Error fetching IT Equipments:', error);
      }
    };

    fetchITEquipments();
  }, []);

  return (
    <div className="view-container">
      <h1>View IT Equipments</h1>
      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>Field</th>
              {itEquipments.map((equipment, index) => (
                <th key={index}>Equipment {index + 1}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Object.keys(itEquipments[0] || {}).map((key) => (
              <tr key={key}>
                <td className="left-column">{key.replace(/_/g, ' ')}</td>
                {itEquipments.map((equipment, index) => (
                  <td key={index} className={index % 2 === 0 ? "right-column" : ""}>
                    {equipment[key]}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ITEquipmentView;
