import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useTable, useSortBy, useFilters } from 'react-table';
import Select from 'react-select';
import { useHistory } from 'react-router-dom';
import 'react-virtualized/styles.css';
import './ITEquipmentView.css';

const ITEquipmentView = () => {
  const [itEquipments, setITEquipments] = useState([]);
  const [columns, setColumns] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const fetchITEquipments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/it-equipments', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        const data = response.data.map(({ createdAt, updatedAt, id, ...rest }) => rest);
        setITEquipments(data);

        const headers = Object.keys(data[0] || {});
        const cols = headers.map((header, index) => ({
          Header: header.replace(/_/g, ' '),
          accessor: header,
          Filter: SelectColumnFilter,
          Cell: EditableCell,
          width: 150, // Default column width
        }));

        setColumns(cols);
      } catch (error) {
        console.error('Error fetching IT Equipments:', error);
      }
    };

    fetchITEquipments();
  }, []);

  const SelectColumnFilter = ({
    column: { filterValue, setFilter, preFilteredRows, id },
  }) => {
    const options = React.useMemo(() => {
      const optionsSet = new Set();
      preFilteredRows.forEach(row => {
        optionsSet.add(row.values[id]);
      });
      return [...optionsSet].map(option => ({ value: option, label: option }));
    }, [id, preFilteredRows]);
    const handleChange = (selectedOptions) => {
      setFilter(selectedOptions ? selectedOptions.map(option => option.value) : undefined);
    };

    return (
      <Select
        value={options.filter(option => filterValue && filterValue.includes(option.value))}
        onChange={handleChange}
        options={options}
        isMulti
        placeholder={`Filter by...`}
        className="itequipment-view-filter-select"
      />
    );
  };

  const Table = ({ columns, data }) => {
    const defaultColumn = {
      Cell: EditableCell,
      Filter: SelectColumnFilter,
      width: 150,
    };
    const {
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow,
    } = useTable(
      {
        columns,
        data,
        defaultColumn,
      },
      useFilters,
      useSortBy
    );

    return (
      <div className="itequipment-view-table-container">
        <table {...getTableProps()} className="itequipment-view-data-table">
          <thead>
            {headerGroups.map(headerGroup => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map(column => (
                  <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                    <div style={{ width: column.width }}>
                      {column.render('Header')}
                      <span>
                        {column.isSorted
                          ? column.isSortedDesc
                            ? ' ðŸ”½'
                            : ' ðŸ”¼'
                          : ''}
                      </span>
                      <div>{column.canFilter ? column.render('Filter') : null}</div>
                    </div>
                  </th>
                ))}
              </tr>
            ))}
          </thead>

          <tbody {...getTableBodyProps()}>
            {rows.map((row, rowIndex) => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()} className={rowIndex % 2 === 0 ? 'itequipment-view-row-even' : 'itequipment-view-row-odd'}>
                  {row.cells.map(cell => (
                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                  ))}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  };

  const EditableCell = ({
    value: initialValue,
    row: { index },
    column: { id },
    updateMyData,
    className,
  }) => {
    const [value, setValue] = React.useState(initialValue);
    const onChange = e => {
      setValue(e.target.value);
    };

    const onBlur = () => {
      updateMyData(index, id, value);
    };

    React.useEffect(() => {
      setValue(initialValue);
    }, [initialValue]);

    return <input value={value} onChange={onChange} onBlur={onBlur} className={className} />;
  };

  return (
    <div className="itequipment-view-container">
      <button className="itequipment-view-back-button" onClick={() => history.push('/it-equipment')}>Back</button>
      <h1 className="itequipment-view-title">View IT Equipments</h1>
      {columns.length > 0 && <Table columns={columns} data={itEquipments} />}
    </div>
  );
};

export default ITEquipmentView;
