// src/components/Dashboard/ExcelEditor.js
import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { useTable, useSortBy, useFilters } from 'react-table';
import { Resizable } from 'react-resizable';
import Select from 'react-select';
import './ExcelEditor.css';

const ExcelEditor = () => {
  const [columns, setColumns] = useState([]);
  const [data, setData] = useState([]);
  const [fileName, setFileName] = useState('');

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setFileName(file.name);

    const reader = new FileReader();
    reader.onload = (event) => {
      const binaryStr = event.target.result;
      const workbook = XLSX.read(binaryStr, { type: 'binary' });

      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(sheet);

      const cols = Object.keys(jsonData[0]).map((key) => ({
        Header: key,
        accessor: key,
        Filter: SelectColumnFilter,
        Cell: EditableCell,
        width: 150, // Default column width
      }));

      setColumns(cols);
      setData(jsonData);
    };
    reader.readAsBinaryString(file);
  };

  const handleCellChange = (rowIndex, columnId, value) => {
    const updatedData = data.map((row, index) => {
      if (index === rowIndex) {
        return {
          ...row,
          [columnId]: value,
        };
      }
      return row;
    });
    setData(updatedData);
  };

  const downloadExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(data);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Sheet1');

    const excelBuffer = XLSX.write(workbook, { bookType: 'xlsx', type: 'array' });
    const blob = new Blob([excelBuffer], { type: 'application/octet-stream' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'modified_' + fileName;
    document.body.appendChild(a);
    a.click();
    a.remove();
  };

  const SelectColumnFilter = ({
    column: { filterValue, setFilter, preFilteredRows, id },
  }) => {
    const options = React.useMemo(() => {
      const optionsSet = new Set();
      preFilteredRows.forEach(row => {
        optionsSet.add(row.values[id]);
      });
      return [...optionsSet].map(option => ({ value: option, label: option }));
    }, [id, preFilteredRows]);

    return (
      <Select
        value={filterValue}
        onChange={setFilter}
        options={options}
        isMulti
        placeholder={`Filter by...`}
        className="filter-select"
      />
    );
  };

  const Table = ({ columns, data }) => {
    const defaultColumn = {
      Cell: EditableCell,
      Filter: SelectColumnFilter,
      width: 150,
    };

    const {
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow,
    } = useTable(
      {
        columns,
        data,
        defaultColumn,
        updateMyData: handleCellChange,
      },
      useFilters,
      useSortBy
    );

    const handleResize = (index) => (e, { size }) => {
      setColumns(old =>
        old.map((col, i) => {
          if (i === index) {
            return { ...col, width: size.width };
          }
          return col;
        })
      );
    };

    return (
      <table {...getTableProps()} className="data-table">
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column, index) => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                  <Resizable
                    width={column.width}
                    height={0}
                    onResize={handleResize(index)}
                    draggableOpts={{ enableUserSelectHack: false }}
                  >
                    <div style={{ width: column.width }}>
                      {column.render('Header')}
                      <span>
                        {column.isSorted
                          ? column.isSortedDesc
                            ? ' ðŸ”½'
                            : ' ðŸ”¼'
                          : ''}
                      </span>
                      <div>{column.canFilter ? column.render('Filter') : null}</div>
                    </div>
                  </Resizable>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map(row => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map(cell => (
                  <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  };

  const EditableCell = ({
    value: initialValue,
    row: { index },
    column: { id },
    updateMyData,
  }) => {
    const [value, setValue] = React.useState(initialValue);

    const onChange = e => {
      setValue(e.target.value);
    };

    const onBlur = () => {
      updateMyData(index, id, value);
    };

    React.useEffect(() => {
      setValue(initialValue);
    }, [initialValue]);

    return <input value={value} onChange={onChange} onBlur={onBlur} />;
  };

  return (
    <div className="excel-editor">
      <input type="file" onChange={handleFileUpload} className="file-input" />
      {columns.length > 0 && (
        <Table columns={columns} data={data} />
      )}
      <button onClick={downloadExcel} className="download-button">Download Modified Excel</button>
    </div>
  );
};

export default ExcelEditor;
