import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';
import { useTable, useSortBy, useFilters } from 'react-table';
import Select from 'react-select';
import 'react-virtualized/styles.css'; // Only import the styles you need
import './ITEquipment.css';

const indexToColumnName = (index) => {
  let columnName = '';
  let dividend = index + 1;

  while (dividend > 0) {
    let modulo = (dividend - 1) % 26;
    columnName = String.fromCharCode(65 + modulo) + columnName;
    dividend = Math.floor((dividend - modulo) / 26);
  }

  return columnName;
};

const ITEquipment = () => {
  const [itEquipments, setITEquipments] = useState([]);
  const [newEquipment, setNewEquipment] = useState({
    categorie: '',
    marque: '',
    model: '',
    code_materiel: '',
    serie: '',
    code_localisation: '',
    code_entite: '',
    date_installation: '',
    fin_garantie: '',
    statut: '',
    type_acquisition: '',
    date_achat: '',
    date_livraison: '',
    fournisseur: '',
    numero_facture: '',
    prix_achat: '',
    numero_appel_offre: '',
    numero_livraison: '',
    cout_maintenance: '',
    emploi_principal: '',
    niveau_criticite: '',
    sla: '',
    date_sortie: '',
  });

  useEffect(() => {
    const fetchITEquipments = async () => {
      try {
        const response = await axios.get('/api/it-equipments');
        setITEquipments(response.data);
        console.log('Fetched IT Equipments:', response.data);
      } catch (error) {
        console.error('Error fetching IT Equipments:', error);
      }
    };

    fetchITEquipments();
  }, []);

  const handleAddEquipment = async () => {
    try {
      console.log('Adding equipment:', newEquipment);
      const response = await axios.post('/api/it-equipments', newEquipment);
      console.log('Response from server:', response.data);
      setITEquipments([...itEquipments, response.data]);
      setNewEquipment({
        categorie: '',
        marque: '',
        model: '',
        code_materiel: '',
        serie: '',
        code_localisation: '',
        code_entite: '',
        date_installation: '',
        fin_garantie: '',
        statut: '',
        type_acquisition: '',
        date_achat: '',
        date_livraison: '',
        fournisseur: '',
        numero_facture: '',
        prix_achat: '',
        numero_appel_offre: '',
        numero_livraison: '',
        cout_maintenance: '',
        emploi_principal: '',
        niveau_criticite: '',
        sla: '',
        date_sortie: '',
      });
      console.log('Equipment added successfully');
    } catch (error) {
      console.error('Error adding IT Equipment:', error);
    }
  };

  const handleDeleteEquipment = async (id) => {
    try {
      await axios.delete(`/api/it-equipments/${id}`);
      setITEquipments(itEquipments.filter(equipment => equipment.id !== id));
      console.log('Equipment deleted successfully');
    } catch (error) {
      console.error('Error deleting IT Equipment:', error);
    }
  };

  const handleChange = (e) => {
    setNewEquipment({ ...newEquipment, [e.target.name]: e.target.value });
  };

  const EditableCell = ({
    value: initialValue,
    row: { index },
    column: { id },
    updateMyData,
  }) => {
    const [value, setValue] = useState(initialValue);

    const onChange = (e) => {
      setValue(e.target.value);
    };

    const onBlur = () => {
      updateMyData(index, id, value);
    };

    useEffect(() => {
      setValue(initialValue);
    }, [initialValue]);

    return <input value={value} onChange={onChange} onBlur={onBlur} />;
  };

  const SelectColumnFilter = ({
    column: { filterValue, setFilter, preFilteredRows, id },
  }) => {
    const options = useMemo(() => {
      const optionsSet = new Set();
      preFilteredRows.forEach((row) => {
        optionsSet.add(row.values[id]);
      });
      return [...optionsSet].map((option) => ({
        value: option,
        label: option,
      }));
    }, [id, preFilteredRows]);

    const handleChange = (selectedOptions) => {
      setFilter(selectedOptions ? selectedOptions.map((option) => option.value) : undefined);
    };

    return (
      <Select
        value={options.filter((option) => filterValue && filterValue.includes(option.value))}
        onChange={handleChange}
        options={options}
        isMulti
        placeholder={`Filter by...`}
      />
    );
  };

  const Table = ({ columns, data }) => {
    const defaultColumn = {
      Cell: EditableCell,
      Filter: SelectColumnFilter,
      width: 150,
    };

    const {
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow,
    } = useTable(
      {
        columns,
        data,
        defaultColumn,
        updateMyData: handleCellChange,
      },
      useFilters,
      useSortBy
    );

    return (
      <table {...getTableProps()} className="data-table">
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              <th>#</th>
              {headerGroup.headers.map((column, index) => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                  <div style={{ width: column.width }}>
                    {indexToColumnName(index)}
                    <div>{column.render('Header')}</div>
                    <span>
                      {column.isSorted
                        ? column.isSortedDesc
                          ? ' ðŸ”½'
                          : ' ðŸ”¼'
                        : ''}
                    </span>
                    <div>{column.canFilter ? column.render('Filter') : null}</div>
                  </div>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, rowIndex) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                <td>{rowIndex + 1}</td>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  };

  const handleCellChange = (rowIndex, columnId, value) => {
    const updatedData = data.map((row, index) => {
      if (index === rowIndex) {
        return {
          ...row,
          [columnId]: value,
        };
      }
      return row;
    });
    setData(updatedData);
  };

  const columns = useMemo(
    () =>
      Object.keys(newEquipment).map((key, index) => ({
        Header: key.replace(/_/g, ' '),
        accessor: `col${index}`,
      })),
    [newEquipment]
  );

  const data = useMemo(
    () =>
      itEquipments.map((equipment) => {
        const rowData = {};
        Object.keys(equipment).forEach((key, index) => {
          rowData[`col${index}`] = equipment[key];
        });
        return rowData;
      }),
    [itEquipments]
  );

  return (
    <div className="it-equipment-manager">
      <h1>IT Equipment Manager</h1>
      <div className="add-equipment">
        <table className="form-table">
          <tbody>
            {Object.keys(newEquipment).map((key) => (
              <tr key={key}>
                <td>
                  <label>{key.replace(/_/g, ' ')}</label>
                </td>
                <td>
                  <input
                    type="text"
                    name={key}
                    value={newEquipment[key]}
                    onChange={handleChange}
                    placeholder={key.replace(/_/g, ' ')}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button onClick={handleAddEquipment}>Add Equipment</button>
      </div>
      {columns.length > 0 && <Table columns={columns} data={data} />}
    </div>
  );
};

export default ITEquipment;
