import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useTable, useSortBy, useFilters } from 'react-table';
import { useHistory } from 'react-router-dom';
import './ITEquipmentView.css';

const ITEquipmentView = () => {
  const [itEquipments, setITEquipments] = useState([]);
  const [columns, setColumns] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const fetchITEquipments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/it-equipments', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        const data = response.data.map(({ createdAt, updatedAt, id, ...rest }) => rest);
        setITEquipments(data);

        const headers = Object.keys(data[0] || {});
        const cols = headers.map((header) => ({
          Header: header.replace(/_/g, ' '),
          accessor: header,
        }));

        setColumns(cols);
      } catch (error) {
        console.error('Error fetching IT Equipments:', error);
      }
    };

    fetchITEquipments();
  }, []);

  const Table = ({ columns, data }) => {
    const defaultColumn = {
      width: 150,
    };
    const {
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow,
    } = useTable(
      {
        columns,
        data,
        defaultColumn,
      },
      useFilters,
      useSortBy
    );

    return (
      <div className="itequipment-view-table-container">
        <table {...getTableProps()} className="itequipment-view-data-table">
          <thead>
            {headerGroups.map(headerGroup => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map(column => (
                  <th {...column.getHeaderProps()}>
                    <div style={{ width: column.width }}>
                      {column.render('Header')}
                    </div>
                  </th>
                ))}
              </tr>
            ))}
          </thead>

          <tbody {...getTableBodyProps()}>
            {rows.map((row, rowIndex) => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()} className={rowIndex % 2 === 0 ? 'itequipment-view-row-even' : 'itequipment-view-row-odd'}>
                  {row.cells.map(cell => (
                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                  ))}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="itequipment-view-container">
      <button className="itequipment-view-back-button" onClick={() => history.push('/it-equipment')}>Back</button>
      <h1 className="itequipment-view-title">View IT Equipments</h1>
      {columns.length > 0 && <Table columns={columns} data={itEquipments} />}
    </div>
  );
};

export default ITEquipmentView;
