// src/components/Shared/Sidebar.js
import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import './Sidebar.css';

const Sidebar = () => {
  const [isActive, setIsActive] = useState(false);
  const { logout } = useAuth();

  const toggleSidebar = () => {
    setIsActive(!isActive);
  };

  return (
    <>
      <button className="sidebar-toggle" onClick={toggleSidebar}>
        â˜° Menu
      </button>
      <div className={`sidebar ${isActive ? 'active' : ''}`}>
        <nav>
          <ul>
            <li>
              <NavLink to="/dashboard" activeClassName="active">
                Dashboard
              </NavLink>
            </li>
            <li>
              <NavLink to="/telecom-packs" activeClassName="active">
                Telecom Packs
              </NavLink>
            </li>
            <li>
              <NavLink to="/telephone-lines" activeClassName="active">
                Telephone Lines
              </NavLink>
            </li>
            <li>
              <NavLink to="/it-equipment" activeClassName="active">
                IT Equipments
              </NavLink>
            </li>
            <li>
              <NavLink to="/edit-excel" activeClassName="active">
                Edit Excel
              </NavLink>
            </li>
          </ul>
        </nav>
        <div className="logout-container">
          <button onClick={logout} className="logout-button">
            Logout
            <span className="logout-icon">ðŸ”’</span>
          </button>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
