import React, { useState, useContext, createContext, useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const auth = useProvideAuth();
  return <AuthContext.Provider value={auth}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  return useContext(AuthContext);
};

const useProvideAuth = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const history = useHistory();

  const login = async (email, password) => {
    try {
      const response = await fetch('http://localhost:5000/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        const decodedToken = JSON.parse(atob(data.token.split('.')[1]));
        localStorage.setItem('token', data.token);
        const loggedInUser = { email, role: decodedToken.user.role, fullName: decodedToken.user.fullName };
        setUser(loggedInUser);
        setLoading(false);
        return loggedInUser;
      } else {
        const error = await response.json();
        throw new Error(error.msg || 'Login failed');
      }
    } catch (error) {
      setLoading(false);
      throw new Error('Server error: ' + error.message);
    }
  };

  const signup = async (fullName, email, password, role) => {
    try {
      const response = await fetch('http://localhost:5000/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ fullName, email, password, role }),
      });

      if (response.ok) {
        const data = await response.json();
        const decodedToken = JSON.parse(atob(data.token.split('.')[1]));
        localStorage.setItem('token', data.token);
        setUser({ email, role: decodedToken.user.role, fullName: decodedToken.user.fullName });
        setLoading(false);
      } else {
        const error = await response.json();
        throw new Error(error.msg || 'Signup failed');
      }
    } catch (error) {
      setLoading(false);
      throw new Error('Server error: ' + error.message);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
    history.push('/login'); // Ensure the user is redirected to login
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        const loggedInUser = { email: decodedToken.user.email, role: decodedToken.user.role, fullName: decodedToken.user.fullName };
        setUser(loggedInUser);
      } catch (error) {
        console.error('Error decoding token:', error);
        localStorage.removeItem('token');
      }
    }
    setLoading(false);
  }, []);

  return {
    user,
    setUser,
    login,
    signup,
    logout,
    loading,
  };
};
