// src/components/Dashboard/ExcelEditor.js
import React, { useState } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';
import DataGrid from 'react-data-grid';

const ExcelEditor = () => {
  const [columns, setColumns] = useState([]);
  const [rows, setRows] = useState([]);
  const [fileName, setFileName] = useState('');

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    setFileName(file.name);

    const formData = new FormData();
    formData.append('file', file);

    const response = await axios.post('/api/excel/upload', formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });

    const uploadedSheet = response.data.sheets[0];
    const cols = Object.keys(uploadedSheet.data[0]).map((key) => ({
      key,
      name: key,
      editable: true,
      sortable: true,
      filterable: true,
    }));
    setColumns(cols);
    setRows(uploadedSheet.data);
  };

  const handleRowsChange = (updatedRows) => {
    setRows(updatedRows);
  };

  const downloadExcel = async () => {
    const sheetData = [
      {
        sheetName: 'Sheet1',
        data: rows,
      },
    ];

    const response = await axios.post('/api/excel/generate', { sheets: sheetData }, { responseType: 'arraybuffer' });
    const blob = new Blob([response.data], { type: response.headers['content-type'] });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'modified_' + fileName;
    document.body.appendChild(a);
    a.click();
    a.remove();
  };

  return (
    <div>
      <input type="file" onChange={handleFileUpload} />
      {columns.length > 0 && (
        <DataGrid
          columns={columns}
          rows={rows}
          onRowsChange={handleRowsChange}
          rowKeyGetter={(row) => row.id || row.key || JSON.stringify(row)}
          sortable
          filterable
        />
      )}
      <button onClick={downloadExcel}>Download Modified Excel</button>
    </div>
  );
};

export default ExcelEditor;
