import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import { useTable, useSortBy, useFilters } from 'react-table';
import Select from 'react-select';
import './TelecomPack.css';
import * as XLSX from 'xlsx';

// Helper function to set default values
const setDefaultValues = (data, defaultValue = '------') => {
  return Object.fromEntries(
    Object.entries(data).map(([key, value]) => {
      if (value === '' || value === null) {
        if (['dateAbonnement', 'dateReengagement', 'dateEtat'].includes(key)) {
          return [key, null];  // Set date fields to null if empty
        } else {
          return [key, defaultValue];  // Set other fields to default value
        }
      }
      return [key, value];
    })
  );
};

const TelecomPack = () => {
  const [telecomPacks, setTelecomPacks] = useState([]);
  const [options, setOptions] = useState({});
  const [newPack, setNewPack] = useState({
    entite: '',
    operateur: '',
    produit: '',
    numero: '',
    etatAbonnement: '',
    dateAbonnement: '',
    dateReengagement: '',
    dateEtat: '',
    observation: '',
  });

  const [isEditing, setIsEditing] = useState(false);
  const [currentPack, setCurrentPack] = useState(null);
  const history = useHistory();

  useEffect(() => {
    const fetchTelecomPacks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/telecom/telecom-packs', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setTelecomPacks(response.data);

        const extractedOptions = response.data.reduce((acc, pack) => {
          for (const key in pack) {
            if (!acc[key]) {
              acc[key] = [];
            }
            if (pack[key] && !acc[key].includes(pack[key])) {
              acc[key].push(pack[key]);
            }
          }
          return acc;
        }, {});
        setOptions(extractedOptions);

      } catch (error) {
        console.error('Error fetching Telecom Packs:', error);
      }
    };

    fetchTelecomPacks();
  }, []);

  const handleAddPack = async () => {
    try {
      const formattedPack = setDefaultValues(newPack);
      const response = await axios.post('http://localhost:5000/api/telecom/telecom-packs', formattedPack, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      const addedPack = response.data;

      setTelecomPacks([...telecomPacks, addedPack]);
      setNewPack({
        entite: '',
        operateur: '',
        produit: '',
        numero: '',
        etatAbonnement: '',
        dateAbonnement: '',
        dateReengagement: '',
        dateEtat: '',
        observation: '',
      });
    } catch (error) {
      console.error('Error adding Telecom Pack:', error);
    }
  };

  const handleDeletePack = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/telecom/telecom-packs/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setTelecomPacks(telecomPacks.filter(pack => pack.id !== id));
    } catch (error) {
      console.error('Error deleting Telecom Pack:', error);
    }
  };

  const handleModifyPack = (pack) => {
    const filteredPack = Object.fromEntries(
      Object.entries(pack).filter(([key, value]) => value !== '' && value !== null)
    );
    setIsEditing(true);
    setCurrentPack({
      ...filteredPack,
      dateAbonnement: formatDate(filteredPack.dateAbonnement),
      dateReengagement: formatDate(filteredPack.dateReengagement),
      dateEtat: formatDate(filteredPack.dateEtat)
    });
  };  

  const handleUpdatePack = async () => {
    try {
      const filteredPack = setDefaultValues(currentPack);
      const response = await axios.put(`http://localhost:5000/api/telecom/telecom-packs/${currentPack.id}`, filteredPack, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      const updatedPack = response.data;
      setTelecomPacks(telecomPacks.map(pack => pack.id === updatedPack.id ? updatedPack : pack));
      setIsEditing(false);
      setCurrentPack(null);
    } catch (error) {
      console.error('Error updating Telecom Pack:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (isEditing) {
      setCurrentPack(prevState => ({
        ...prevState,
        [name]: value,
      }));
    } else {
      setNewPack(prevState => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  const handleDropdownChange = (selectedOption, name) => {
    if (isEditing) {
      setCurrentPack(prevState => ({
        ...prevState,
        [name]: selectedOption.value,
      }));
    } else {
      setNewPack(prevState => ({
        ...prevState,
        [name]: selectedOption.value,
      }));
    }
  };
  const formatDate = (dateString) => {
    if (!dateString || dateString === '------') return '';
    const date = new Date(dateString);
    return date.toISOString().split('T')[0];
  };


  const columns = React.useMemo(() => [
    {
      Header: '#',
      accessor: (row, i) => i + 1,
      disableFilters: true,
      disableSortBy: true,
    },
    {
      Header: 'Actions',
      accessor: 'actions',
      disableFilters: true,
      disableSortBy: true,
    },
    ...Object.keys(newPack).map((key) => ({
      Header: key.replace(/_/g, ' '),
      accessor: key,
      Filter: SelectColumnFilter,
    })),
  ], []);

  const data = React.useMemo(() => telecomPacks.map((pack, index) => ({
    rowNumber: index + 1,
    actions: (
      <div>
        <button className="modify-button" onClick={() => handleModifyPack(pack)}>Modify</button>
        <button className="delete-button" onClick={() => handleDeletePack(pack.id)}>Delete</button>
      </div>
    ),
    ...pack,
    dateAbonnement: formatDate(pack.dateAbonnement),
    dateReengagement: formatDate(pack.dateReengagement),
    dateEtat: formatDate(pack.dateEtat),
  })), [telecomPacks]);

  const dropdownFields = ['entite', 'operateur', 'produit', 'etatAbonnement'];

  return (
    <div className="telecom-pack-manager">
      <button className="telecompack-modify-back-button" onClick={() => history.goBack()}>
        &#x21a9;
      </button>
      <h1>Telecom Pack Manager</h1>
      <div className="add-pack">
        <table className="form-table telecom-pack-form-table">
          <tbody>
            {Object.keys(newPack).map((key, index) => (
              index % 3 === 0 && (
                <tr key={index}>
                  {Object.keys(newPack).slice(index, index + 3).map(innerKey => (
                    <td key={innerKey}>
                      <label className="telecom-pack-form-label">{innerKey.replace(/_/g, ' ')}</label>
                      {dropdownFields.includes(innerKey) ? (
                        <CustomDropdown
                          name={innerKey}
                          value={isEditing ? currentPack[innerKey] : newPack[innerKey]}
                          options={options[innerKey] || []}
                          onChange={handleChange}
                          placeholder={`Enter/Select ${innerKey.replace(/_/g, ' ')}`}
                        />
                      ) : (
                        <input
                          type={innerKey.includes('date') ? 'date' : 'text'}
                          name={innerKey}
                          value={isEditing ? currentPack[innerKey] : newPack[innerKey]}
                          onChange={handleChange}
                          className="input-field"
                          placeholder={`Enter ${innerKey.replace(/_/g, ' ')}`}
                        />
                      )}
                    </td>
                  ))}
                </tr>
              )
            ))}
          </tbody>
        </table>
        {isEditing ? (
          <button onClick={handleUpdatePack}>Update Pack</button>
        ) : (
          <button onClick={handleAddPack}>Add Pack</button>
        )}
      </div>
      <div className="table-container">
        <Table columns={columns} data={data} />
      </div>
    </div>
  );
};

const SelectColumnFilter = ({ column: { filterValue, setFilter, preFilteredRows, id } }) => {
  const options = React.useMemo(() => {
    const optionsSet = new Set();
    preFilteredRows.forEach(row => {
      optionsSet.add(row.values[id]);
    });
    return [...optionsSet].map(option => ({ value: option, label: option }));
  }, [id, preFilteredRows]);
  const handleChange = (selectedOptions) => {
    setFilter(selectedOptions ? selectedOptions.map(option => option.value) : undefined);
  };

  return (
    <Select
      value={options.filter(option => filterValue && filterValue.includes(option.value))}
      onChange={handleChange}
      options={options}
      isMulti
      placeholder={`Filter by...`}
      className="filter-select"
    />
  );
};

const Table = ({ columns, data }) => {
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable(
    {
      columns,
      data,
    },
    useFilters,
    useSortBy
  );

  return (
    <table {...getTableProps()} className="data-table telecompack-view-data-table">
      <thead>
        {headerGroups.map(headerGroup => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map(column => (
              <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                <div>
                  {column.render('Header')}
                  <span>
                    {column.isSorted
                      ? column.isSortedDesc
                        ? ' 🔽'
                        : ' 🔼'
                      : ''}
                  </span>
                  <div>{column.canFilter ? column.render('Filter') : null}</div>
                </div>
              </th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row, index) => {
          prepareRow(row);
          return (
            <tr {...row.getRowProps()} className={index % 2 === 0 ? "telecompack-view-row-even" : "telecompack-view-row-odd"}>
              {row.cells.map(cell => (
                <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
              ))}
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

const CustomDropdown = ({ name, value, options, onChange, placeholder }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleSelect = (option) => {
    onChange({ target: { name, value: option } });
    setIsOpen(false);
  };

  return (
    <div className="dropdown-container">
      <input
        type="text"
        name={name}
        value={value}
        onChange={onChange}
        placeholder={placeholder} // Add placeholder
      />
      <div className="dropdown-arrow" onClick={() => setIsOpen(!isOpen)}>
        ▼
      </div>
      {isOpen && (
        <div className="dropdown-menu">
          {options.map((option, index) => (
            <div key={index} className="dropdown-option" onClick={() => handleSelect(option)}>
              {option}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default TelecomPack;
