import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';

const ExcelManipulation = ({ tables }) => {
  const [selectedTable, setSelectedTable] = useState('');
  const [tableOptions, setTableOptions] = useState([]);

  useEffect(() => {
    if (tables && Object.keys(tables).length > 0) {
      setTableOptions(Object.keys(tables));
    }
  }, [tables]);

  const handleExport = () => {
    const tableData = tables[selectedTable];
    if (tableData) {
      const worksheet = XLSX.utils.json_to_sheet(tableData);
      const workbook = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(workbook, worksheet, selectedTable);
      XLSX.writeFile(workbook, `${selectedTable}.xlsx`);
    } else {
      console.log("No data available for the selected table.");
    }
  };

  return (
    <div>
      <h1>Excel Manipulation</h1>
      <label htmlFor="table-select">Choose a table to export:</label>
      <select
        id="table-select"
        value={selectedTable}
        onChange={(e) => setSelectedTable(e.target.value)}
      >
        <option value="" disabled>Select Table</option>
        {tableOptions.length > 0 ? (
          tableOptions.map((table) => (
            <option key={table} value={table}>
              {table}
            </option>
          ))
        ) : (
          <option value="" disabled>No tables available</option>
        )}
      </select>
      <button onClick={handleExport} disabled={!selectedTable}>
        Export to Excel
      </button>
    </div>
  );
};

export default ExcelManipulation;
