import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ExcelEditor.css';

const ExcelEditor = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [table, setTable] = useState('telephone_lines');
  const [schema, setSchema] = useState([]);
  const [message, setMessage] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleTableChange = (event) => {
    setTable(event.target.value);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file first!');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      await axios.post(`http://localhost:5000/api/upload/${table}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage('File uploaded successfully!');
    } catch (error) {
      console.error('Error uploading file:', error);
      setMessage('Failed to upload data');
    }
  };

  useEffect(() => {
    const fetchSchema = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/schema/${table}`);
        setSchema(response.data.schema);
      } catch (error) {
        console.error('Error fetching schema:', error);
        setMessage('Failed to fetch schema');
      }
    };

    if (table) {
      fetchSchema();
    }
  }, [table]);

  return (
    <div className="excel-editor-container">
      <h2>Excel File Upload</h2>
      <div className="form-group">
        <label htmlFor="tableSelect">Select Table</label>
        <select id="tableSelect" value={table} onChange={handleTableChange}>
          <option value="telephone_lines">Telephone Lines</option>
          <option value="telecom_pack">Telecom Packs</option>
          <option value="it_equipments">IT Equipments</option>
        </select>
      </div>
      <div className="form-group">
        <label htmlFor="fileUpload">Select Excel file</label>
        <input type="file" id="fileUpload" onChange={handleFileChange} />
      </div>
      <button onClick={handleUpload}>Upload</button>
      {message && <p>{message}</p>}
      {schema.length > 0 ? (
        <div>
          <h3>Expected Columns:</h3>
          <ul>
            {schema.map((column, index) => (
              <li key={index}>{column}</li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No schema available</p>
      )}
    </div>
  );
};

export default ExcelEditor;
