import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useTable, useSortBy, useFilters, useGlobalFilter } from 'react-table';
import Select from 'react-select';
import { useHistory } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaSortUp, FaSortDown, FaSort } from 'react-icons/fa';

import './ITEquipmentView.css';

const ITEquipmentView = () => {
  const [itEquipments, setITEquipments] = useState([]);
  const [originalData, setOriginalData] = useState([]);
  const [columns, setColumns] = useState([]);
  const [viewType, setViewType] = useState('general');
  const [filters, setFilters] = useState({});
  const [sortBy, setSortBy] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const history = useHistory();

  useEffect(() => {
    fetchITEquipments();
  }, []);

  useEffect(() => {
    setCurrentPage(1); // Reset to the first page whenever filters or sort change
  }, [filters, sortBy]);

  const setDefaultValues = (data, defaultValue = '------', defaultNumber = 0) => {
    const updatedData = { ...data };
    for (let key in updatedData) {
      if (updatedData[key] === '' || updatedData[key] === null) {
        if (['date_installation', 'fin_garantie', 'date_achat', 'date_livraison', 'date_sortie'].includes(key)) {
          updatedData[key] = null;
        } else if (key === 'prix_achat') {
          updatedData[key] = defaultNumber;
        } else {
          updatedData[key] = defaultValue;
        }
      }
    }
    return updatedData;
  };

  const fetchITEquipments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/it-equipments', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.data && Array.isArray(response.data.equipments)) {
        const data = response.data.equipments.map(({ createdAt, updatedAt, id, ...rest }) => setDefaultValues(rest));
        setITEquipments(data);
        setOriginalData(data);

        const headers = Object.keys(data[0] || {});
        const cols = [
          {
            Header: '#',
            accessor: (row, i) => i + 1,
            disableFilters: true,
            disableSortBy: true,
            width: 50,
          },
          ...headers.map((header) => ({
            Header: header.replace(/_/g, ' '),
            accessor: header,
            Filter: props => <SelectColumnFilter {...props} originalData={data} />,
          })),
        ];

        setColumns(cols);
      } else {
        toast.error('Error fetching IT equipments');
      }
    } catch (error) {
      toast.error('Error fetching IT equipments: ' + error.message);
    }
  };

  const applyFilters = (data, filters) => {
    return data.filter(row => {
      return Object.entries(filters).every(([id, filterValues]) => {
        if (!filterValues || filterValues.length === 0) return true;
        const rowValue = row[id] != null ? String(row[id]).toLowerCase() : '';
        return filterValues.some(val => rowValue.includes(String(val).toLowerCase()));
      });
    });
  };

  const applySorting = (data, sortBy) => {
    if (!sortBy.length) return data;

    const sortByField = sortBy[0];
    return data.sort((a, b) => {
      const aValue = a[sortByField.id] != null ? a[sortByField.id].toString() : '';
      const bValue = b[sortByField.id] != null ? b[sortByField.id].toString() : '';
      if (aValue < bValue) {
        return sortByField.desc ? 1 : -1;
      }
      if (aValue > bValue) {
        return sortByField.desc ? -1 : 1;
      }
      return 0;
    });
  };

  const filteredAndSortedData = useMemo(() => {
    let data = applyFilters(originalData, filters);
    data = applySorting(data, sortBy);
    return data;
  }, [originalData, filters, sortBy]);

  const totalPages = useMemo(() => {
    return Math.ceil(filteredAndSortedData.length / rowsPerPage);
  }, [filteredAndSortedData, rowsPerPage]);

  const currentRows = useMemo(() => {
    const startIndex = (currentPage - 1) * rowsPerPage;
    const endIndex = startIndex + rowsPerPage;
    return filteredAndSortedData.slice(startIndex, endIndex);
  }, [filteredAndSortedData, currentPage, rowsPerPage]);

  const columnsWithRowNumber = useMemo(() => {
    const rowNumberColumn = {
      Header: '#',
      id: 'rowNumber',
      accessor: (row, i) => (currentPage - 1) * rowsPerPage + i + 1,
      disableFilters: true,
      disableSortBy: true,
      width: 50,
    };

    const filteredColumns = columns.filter(col => col.Header !== '#');
    return [
      rowNumberColumn,
      ...filteredColumns.map(col => ({
        ...col,
        Filter: props => <SelectColumnFilter {...props} originalData={originalData} />,
      }))
    ];
  }, [columns, currentPage, rowsPerPage, originalData]);

  const handlePageNumberClick = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setCurrentPage(1); // Reset to the first page only
  };

  const handleDelete = (filterKey, value) => {
    setFilters(prev => {
      const updatedFilters = { ...prev };
      updatedFilters[filterKey] = updatedFilters[filterKey].filter(val => val !== value);
      if (updatedFilters[filterKey].length === 0) {
        delete updatedFilters[filterKey];
      }
      return updatedFilters;
    });
  };

  const Table = ({ columns, data }) => {
    const defaultColumn = {
      minWidth: 30,
      width: 150,
      maxWidth: 400,
    };

    const {
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow,
    } = useTable(
      {
        columns,
        data,
        defaultColumn,
      },
      useGlobalFilter,
      useFilters,
      useSortBy
    );

    return (
      <div className="itequipment-view-table-container">
        <table {...getTableProps()} className="itequipment-view-data-table">
          <thead>
            {headerGroups.map(headerGroup => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map(column => (
                  <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                    <div style={{ width: column.width }}>
                      {column.render('Header')}
                      <span>
                        {column.isSorted ? (
                          column.isSortedDesc ? (
                            <FaSortDown />
                          ) : (
                            <FaSortUp />
                          )
                        ) : (
                          <FaSort />
                        )}
                      </span>
                      <div>
                        {column.canFilter ? column.render('Filter') : null}
                      </div>
                    </div>
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map((row, rowIndex) => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()} className={rowIndex % 2 === 0 ? 'itequipment-view-row-even' : 'itequipment-view-row-odd'}>
                {row.cells.map(cell => (
                  <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                ))}
              </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  };

  const exportToExcel = (data) => {
    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "ITEquipments");
    XLSX.writeFile(wb, "ITEquipments.xlsx");
  };

  return (
    <div className="itequipment-view-container">
      <ToastContainer />
      <button className="itequipment-view-back-button" onClick={() => history.push('/it-equipment')}>
        &#x21a9;
      </button>
      <h1 className="itequipment-view-title">Afficher IT Equipments</h1>
      <button
        className="itequipment-view-toggle-button"
        onClick={() => {
          if (viewType === 'general') {
            setViewType('filtered');
            setITEquipments(filteredAndSortedData);
          } else {
            setViewType('general');
            setITEquipments(originalData);
          }
        }}
      >
        {viewType === 'general' ? 'Afficher les équipements filtrés' : 'Afficher les équipements informatiques généraux'}
      </button>
      <div className="itequipment-view-selected-filters-container">
        <h3>Selected Filters:</h3>
        {Object.keys(filters).map((filterKey) => (
          filters[filterKey].map((filterValue, index) => (
            <span key={`${filterKey}-${index}`} className="itequipment-view-filter-chip">
              {`${filterKey}: ${filterValue}`} <button onClick={() => handleDelete(filterKey, filterValue)}>x</button>
            </span>
          ))
        ))}
      </div>
      {columns.length > 0 && (
        <Table
          columns={columnsWithRowNumber}
          data={currentRows}
        />
      )}
      <div className="pagination-controls">
        <button onClick={() => handlePageNumberClick(1)} disabled={currentPage === 1}>{'<<'}</button>
        <button onClick={() => handlePageNumberClick(currentPage - 1)} disabled={currentPage === 1}>{'Précédent'}</button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button onClick={() => handlePageNumberClick(currentPage + 1)} disabled={currentPage === totalPages}>{'Suivant'}</button>
        <button onClick={() => handlePageNumberClick(totalPages)} disabled={currentPage === totalPages}>{'>>'}</button>
        <select value={rowsPerPage} onChange={handleRowsPerPageChange}>
          <option value={10}>Show 10</option>
          <option value={25}>Show 25</option>
          <option value={50}>Show 50</option>
          <option value={100}>Show 100</option>
        </select>
      </div>
      <div className="itequipment-view-footer">
        <button
          className="itequipment-view-export-button"
          onClick={() => exportToExcel(filteredAndSortedData)}
        >
          Export to Excel
        </button>
      </div>
    </div>
  );
};

const SelectColumnFilter = ({
  column: { filterValue = [], setFilter, id },
  originalData
}) => {
  const [selectedOptions, setSelectedOptions] = useState(() => 
    filterValue.map(val => ({ value: val, label: val }))
  );

  const options = useMemo(() => {
    const optionsSet = new Set();
    originalData.forEach(row => {
      if (row[id] !== null && row[id] !== '------') {
        optionsSet.add(row[id]);
      }
    });
    return [...optionsSet].map(option => ({ value: option, label: option }));
  }, [id, originalData]);

  const handleChange = (selected) => {
    const values = selected ? selected.map(option => option.value) : [];
    setSelectedOptions(selected);
    setFilter(values.length ? values : undefined);
  };

  const handleDelete = (filterKey, value) => {
    const updatedOptions = selectedOptions.filter(option => option.value !== value);
    const values = updatedOptions.map(option => option.value);
    setSelectedOptions(updatedOptions);
    setFilter(values.length ? values : undefined);
  };

  return (
    <div className="itequipment-view-filter-container">
      <Select
        value={selectedOptions}
        onChange={handleChange}
        options={options}
        isMulti
        placeholder={'Filter by...'}
        className="itequipment-view-filter-select"
      />
      {selectedOptions.length > 0 && (
        <div className="itequipment-view-selected-filters">
          {selectedOptions.map((option, index) => (
            <span key={index} className="itequipment-view-filter-chip">
              {option.value} <button onClick={() => handleDelete(id, option.value)}>x</button>
            </span>
          ))}
        </div>
      )}
    </div>
  );
};

export default ITEquipmentView;
