import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import axios from 'axios';
import './ExcelEditor.css';

const ExcelEditor = () => {
  const [columns, setColumns] = useState([]);
  const [data, setData] = useState([]);
  const [fileName, setFileName] = useState('');
  const [message, setMessage] = useState('');
  const [selectedTable, setSelectedTable] = useState('');

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setFileName(file.name);
    const reader = new FileReader();
    reader.onload = (event) => {
      const binaryStr = event.target.result;
      const workbook = XLSX.read(binaryStr, { type: 'binary' });

      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(sheet);

      setData(jsonData);
    };
    reader.readAsBinaryString(file);
  };

  const handleUpload = async () => {
    if (!selectedTable) {
      setMessage('Please select a table');
      return;
    }

    try {
      const response = await axios.post(`http://localhost:5000/api/upload/${selectedTable}`, { data }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      setMessage('File uploaded successfully');
    } catch (error) {
      setMessage('Failed to upload file');
    }
  };

  const fetchSchema = async (table) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/schema/${table}`);
      setColumns(response.data);
    } catch (error) {
      setMessage('Failed to fetch schema');
    }
  };

  useEffect(() => {
    if (selectedTable) {
      fetchSchema(selectedTable);
    }
  }, [selectedTable]);

  return (
    <div className="excel-editor-container">
      <h1 className="title">Excel File Upload</h1>
      <div className="table-select">
        <label>Select Table:</label>
        <select value={selectedTable} onChange={(e) => setSelectedTable(e.target.value)}>
          <option value="">-- Select Table --</option>
          <option value="it_equipments">IT Equipments</option>
          <option value="telecom_pack">Telecom Packs</option>
          <option value="telephone_lines">Telephone Lines</option>
        </select>
      </div>
      <div className="upload-section">
        <label className="file-label">Select Excel file
          <input type="file" onChange={handleFileUpload} className="file-input" />
        </label>
        <button onClick={handleUpload} className="upload-button">Upload</button>
      </div>
      {message && <p className="message">{message}</p>}
      {columns.length > 0 && (
        <div className="schema-section">
          <h2 className="schema-title">Schema</h2>
          <ul className="schema-list">
            {columns.map((col, index) => (
              <li key={index} className="schema-item">{col}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ExcelEditor;
