import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ITEquipment.css';

const ITEquipment = () => {
  const [itEquipments, setITEquipments] = useState([]);
  const [newEquipment, setNewEquipment] = useState({
    categorie: '',
    marque: '',
    model: '',
    code_materiel: '',
    serie: '',
    code_localisation: '',
    code_entite: '',
    date_installation: '',
    fin_garantie: '',
    statut: '',
    type_acquisition: '',
    date_achat: '',
    date_livraison: '',
    fournisseur: '',
    numero_facture: '',
    prix_achat: '',
    numero_appel_offre: '',
    numero_livraison: '',
    cout_maintenance: '',
    emploi_principal: '',
    niveau_criticite: '',
    sla: '',
    date_sortie: '',
  });

  useEffect(() => {
    const fetchITEquipments = async () => {
      try {
        const response = await axios.get('/it-equipments');
        setITEquipments(response.data);
      } catch (error) {
        console.error('Error fetching IT Equipments:', error);
      }
    };

    fetchITEquipments();
  }, []);

  const handleAddEquipment = async () => {
    try {
      const response = await axios.post('/it-equipments', newEquipment);
      setITEquipments([...itEquipments, response.data]);
      setNewEquipment({
        categorie: '',
        marque: '',
        model: '',
        code_materiel: '',
        serie: '',
        code_localisation: '',
        code_entite: '',
        date_installation: '',
        fin_garantie: '',
        statut: '',
        type_acquisition: '',
        date_achat: '',
        date_livraison: '',
        fournisseur: '',
        numero_facture: '',
        prix_achat: '',
        numero_appel_offre: '',
        numero_livraison: '',
        cout_maintenance: '',
        emploi_principal: '',
        niveau_criticite: '',
        sla: '',
        date_sortie: '',
      });
    } catch (error) {
      console.error('Error adding IT Equipment:', error);
    }
  };

  const handleDeleteEquipment = async (id) => {
    try {
      await axios.delete(`/it-equipments/${id}`);
      setITEquipments(itEquipments.filter(equipment => equipment.id !== id));
    } catch (error) {
      console.error('Error deleting IT Equipment:', error);
    }
  };

  const handleChange = (e) => {
    setNewEquipment({ ...newEquipment, [e.target.name]: e.target.value });
  };

  return (
    <div className="it-equipment-manager">
      <h1>IT Equipment Manager</h1>
      <div className="add-equipment">
        {Object.keys(newEquipment).map((key) => (
          <input
            key={key}
            type="text"
            name={key}
            value={newEquipment[key]}
            onChange={handleChange}
            placeholder={key.replace(/_/g, ' ')}
          />
        ))}
        <button onClick={handleAddEquipment}>Add Equipment</button>
      </div>
      <div className="table-container">
        <table className="data-table vertical">
          <thead>
            <tr>
              <th>#</th>
              <th>Categorie</th>
              <th>Marque</th>
              <th>Model</th>
              <th>Code Materiel</th>
              <th>Serie</th>
              <th>Code Localisation</th>
              <th>Code Entite</th>
              <th>Date Installation</th>
              <th>Fin Garantie</th>
              <th>Statut</th>
              <th>Type Acquisition</th>
              <th>Date Achat</th>
              <th>Date Livraison</th>
              <th>Fournisseur</th>
              <th>Numero Facture</th>
              <th>Prix Achat</th>
              <th>Numero Appel Offre</th>
              <th>Numero Livraison</th>
              <th>Cout Maintenance</th>
              <th>Emploi Principal</th>
              <th>Niveau Criticite</th>
              <th>SLA</th>
              <th>Date Sortie</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {itEquipments.map((equipment, index) => (
              <tr key={equipment.id}>
                <td>{index + 1}</td>
                <td>{equipment.categorie}</td>
                <td>{equipment.marque}</td>
                <td>{equipment.model}</td>
                <td>{equipment.code_materiel}</td>
                <td>{equipment.serie}</td>
                <td>{equipment.code_localisation}</td>
                <td>{equipment.code_entite}</td>
                <td>{equipment.date_installation}</td>
                <td>{equipment.fin_garantie}</td>
                <td>{equipment.statut}</td>
                <td>{equipment.type_acquisition}</td>
                <td>{equipment.date_achat}</td>
                <td>{equipment.date_livraison}</td>
                <td>{equipment.fournisseur}</td>
                <td>{equipment.numero_facture}</td>
                <td>{equipment.prix_achat}</td>
                <td>{equipment.numero_appel_offre}</td>
                <td>{equipment.numero_livraison}</td>
                <td>{equipment.cout_maintenance}</td>
                <td>{equipment.emploi_principal}</td>
                <td>{equipment.niveau_criticite}</td>
                <td>{equipment.sla}</td>
                <td>{equipment.date_sortie}</td>
                <td>
                  <button onClick={() => handleDeleteEquipment(equipment.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ITEquipment;
