import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import { useTable, useSortBy, useFilters } from 'react-table';
import Select from 'react-select';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

import './ITEquipment.css';

// Helper function to set default values
const setDefaultValues = (data, defaultValue = '------') => {
  return Object.fromEntries(
    Object.entries(data).map(([key, value]) => {
      if (value === '' || value === null) {
        if (['date_installation', 'fin_garantie', 'date_achat', 'date_livraison', 'date_sortie'].includes(key)) {
          return [key, null];  // Set date fields to null if empty
        } else {
          return [key, defaultValue];  // Set other fields to default value
        }
      }
      return [key, value];
    })
  );
};

const formatDate = (date) => {
  if (!date) return '';
  const d = new Date(date);
  const month = `${d.getMonth() + 1}`.padStart(2, '0');
  const day = `${d.getDate()}`.padStart(2, '0');
  const year = d.getFullYear();
  return [year, month, day].join('-');
};

const ITEquipment = () => {
  const [itEquipments, setITEquipments] = useState([]);
  const [options, setOptions] = useState({});
  const [allITEquipments, setAllITEquipments] = useState([]); // Store all data for filtering and sorting
  const [newEquipment, setNewEquipment] = useState({
    categorie: '',
    marque: '',
    model: '',
    code_materiel: '',
    serie: '',
    code_localisation: '',
    code_entite: '',
    date_installation: '',
    fin_garantie: '',
    statut: '',
    type_acquisition: '',
    date_achat: '',
    date_livraison: '',
    fournisseur: '',
    numero_facture: '',
    prix_achat: '',
    numero_appel_offre: '',
    numero_livraison: '',
    cout_maintenance: '',
    emploi_principal: '',
    niveau_criticite: '',
    sla: '',
    date_sortie: '',
    commentaire: '',  // Add this field
  });

  const [isEditing, setIsEditing] = useState(false);
  const [currentEquipment, setCurrentEquipment] = useState(null);
  const history = useHistory();

  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(100); // Define the page size
  const [totalPages, setTotalPages] = useState(1);
  const [filters, setFilters] = useState({});
  const [sortBy, setSortBy] = useState([]);

  const fetchAllITEquipments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/it-equipments', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setAllITEquipments(response.data.equipments);
      setTotalPages(Math.ceil(response.data.equipments.length / pageSize));
      setOptions(response.data.uniqueValues); // Set unique values for filters
    } catch (error) {
      console.error('Error fetching IT Equipments:', error);
    }
  };

  const fetchITEquipments = async (page = 1, pageSize = 100) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/it-equipments?page=${page}&pageSize=${pageSize}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setITEquipments(response.data.equipments);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      console.error('Error fetching IT Equipments:', error);
    }
  };


  useEffect(() => {
    fetchAllITEquipments(); // Fetch all data for filters and sorters
    fetchITEquipments(currentPage, pageSize); // Fetch paginated data for display
  }, [currentPage, pageSize]);


  const handleAddEquipment = async () => {
    if (newEquipment.statut === 'REFORME' && !newEquipment.date_sortie) {
      alert('The "date sortie" field must be filled if the statut is "reforme".');
      return;
    }

    try {
      const formattedEquipment = setDefaultValues(newEquipment);
      console.log('Adding equipment:', formattedEquipment);
      const response = await axios.post('http://localhost:5000/api/it-equipments', formattedEquipment, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      console.log('Response from server:', response.data);

      const addedEquipment = response.data;

      setITEquipments([...itEquipments, addedEquipment]);
      setNewEquipment({
        categorie: '',
        marque: '',
        model: '',
        code_materiel: '',
        serie: '',
        code_localisation: '',
        code_entite: '',
        date_installation: '',
        fin_garantie: '',
        statut: '',
        type_acquisition: '',
        date_achat: '',
        date_livraison: '',
        fournisseur: '',
        numero_facture: '',
        prix_achat: '',
        numero_appel_offre: '',
        numero_livraison: '',
        cout_maintenance: '',
        emploi_principal: '',
        niveau_criticite: '',
        sla: '',
        date_sortie: '',
        commentaire: '',  // Reset this field
      });
      console.log('Equipment added successfully');
    } catch (error) {
      console.error('Error adding IT Equipment:', error);
    }
  };

  const handleDeleteEquipment = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/it-equipments/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setITEquipments(itEquipments.filter(equipment => equipment.id !== id));
      console.log('Equipment deleted successfully');
    } catch (error) {
      console.error('Error deleting IT Equipment:', error);
    }
  };

  const handleModifyEquipment = (equipment) => {
    const filteredEquipment = Object.fromEntries(
      Object.entries(equipment).filter(([key, value]) => value !== '' && value !== null)
    );
    setIsEditing(true);
    setCurrentEquipment(filteredEquipment);
  };

  const handleUpdateEquipment = async () => {
    if (currentEquipment.statut === 'REFORME' && !currentEquipment.date_sortie) {
      alert('The "date sortie" field must be filled if the statut is "reforme".');
      return;
    }

    try {
      const filteredEquipment = setDefaultValues(currentEquipment);
      console.log('Updating equipment:', filteredEquipment);
      const response = await axios.put(`http://localhost:5000/api/it-equipments/${currentEquipment.id}`, filteredEquipment, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      console.log('Response from server:', response.data);
      const updatedEquipment = response.data;
      setITEquipments(itEquipments.map(equipment => equipment.id === updatedEquipment.id ? updatedEquipment : equipment));
      setIsEditing(false);
      setCurrentEquipment(null);
      console.log('Equipment updated successfully');
    } catch (error) {
      console.error('Error updating IT Equipment:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (isEditing) {
      setCurrentEquipment(prevState => ({
        ...prevState,
        [name]: value,
      }));
    } else {
      setNewEquipment(prevState => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  const handleDateChange = (name, date) => {
    const formattedDate = date ? formatDate(date) : '';
    if (isEditing) {
      setCurrentEquipment(prevState => ({
        ...prevState,
        [name]: formattedDate,
      }));
    } else {
      setNewEquipment(prevState => ({
        ...prevState,
        [name]: formattedDate,
      }));
    }
  };

  const filteredAndSortedData = React.useMemo(() => {
    let filteredData = allITEquipments; // Use all equipment data for filtering
    
    // Apply filters
    Object.keys(filters).forEach(id => {
      const value = filters[id];
      if (value) {
        filteredData = filteredData.filter(row => {
          return row[id].toString().toLowerCase().includes(value.toLowerCase());
